import React, { Component } from "react";
import "./Person.css";
class Person extends Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.state = {
      name: "John",
      age: 23,
    };
  }
  nameChangeHandler(){
    this.setState({
      name: "Madison"
    });
  }
  ageChangeHandler = () =>{
    this.setState({
      age: "40"
    });
  }
  render() {
    return (
      <div className="PersonBox">
        <h1 style={{color: 'red'}}>
        My name is {this.state.name} and My age is {this.state.age}
        </h1>
        <br></br>
        <button  style={{color: 'red'}} onClick={() => this.nameChangeHandler()}> Change Name</button>  
        <button onClick={this.ageChangeHandler}>Change Age</button>
      </div>
    );
  }
}

export default Person;
